// src/pages/Home.jsx
import React from 'react';
import { Typography, Box } from '@mui/material';
import { useAuth } from '../context/AuthContext';
import TrendingSection from '../components/TrendingSection';
import SearchBar from '../components/SearchBar'; // Assume you have one
import MovieResults from '../components/MovieResults'; // Results from Search

export default function Home() {
  const { currentUser } = useAuth();

  return (
    <Box>
      <TrendingSection />
      {currentUser ? (
        <>
          <Typography variant="h5" sx={{ m: 2 }}>Search Movies</Typography>
          <SearchBar />
          <MovieResults />
        </>
      ) : (
        <Typography variant="body1" sx={{ m: 2 }}>
          Login to search and explore movie details or add to favorites.
        </Typography>
      )}
    </Box>
  );
}
