// src/components/MovieCard.jsx
import React from 'react';
import { Card, CardContent, CardMedia, IconButton, Typography, Box } from '@mui/material';
import FavoriteIcon from '@mui/icons-material/Favorite';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

export default function MovieCard({ movie }) {
  const navigate = useNavigate();
  const { isAuthenticated } = useAuth();

  const handleFavoriteClick = () => {
    if (!isAuthenticated) {
      navigate('/login');
    } else {
      // Add logic to handle favoriting the movie
      console.log('Movie favorited:', movie.title);
    }
  };

  const handleCardClick = () => {
    navigate(`/movie/${movie.id}`);
  };

  return (
    <Card sx={{ width: 200, m: 1, position: 'relative' }}>
      <CardMedia
        component="img"
        height="300"
        image={`https://image.tmdb.org/t/p/w300${movie.poster_path}`}
        alt={movie.title}
        onClick={handleCardClick}
        sx={{ cursor: 'pointer' }}
      />
      <CardContent>
        <Typography variant="subtitle1" component="div" noWrap>
          {movie.title}
        </Typography>
        <Box sx={{ position: 'absolute', top: 5, right: 5 }}>
          <IconButton onClick={handleFavoriteClick} aria-label="add to favorites">
            <FavoriteIcon color="error" />
          </IconButton>
        </Box>
      </CardContent>
    </Card>
  );
}
