// ✅ AuthContext with basic signup/login handling
// src/context/AuthContext.jsx
import React, { createContext, useContext, useState, useEffect } from 'react';
import { loadFromLocalStorage, saveToLocalStorage, removeFromLocalStorage } from '../utils/localStorageHelpers';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedUser = loadFromLocalStorage('authUser');
    if (storedUser) setUser(storedUser);
  }, []);

  const login = async (email, password, rememberMe = false) => {
    const storedUsers = loadFromLocalStorage('users') || [];
    const existingUser = storedUsers.find(
      (user) => user.email === email && user.password === password
    );

    if (existingUser) {
      setUser(existingUser);
      if (rememberMe) saveToLocalStorage('authUser', existingUser);
      return true;
    }
    return false;
  };

  const signup = async (username, email, password) => {
    const users = loadFromLocalStorage('users') || [];
    const existingUser = users.find((user) => user.email === email);
    if (existingUser) {
      throw new Error('Email already exists');
    }
    const newUser = { username, email, password };
    const updatedUsers = [...users, newUser];
    saveToLocalStorage('users', updatedUsers);
    setUser(newUser);
    saveToLocalStorage('authUser', newUser);
  };

  const logout = () => {
    setUser(null);
    removeFromLocalStorage('authUser');
  };

  return (
    <AuthContext.Provider value={{ isAuthenticated: !!user, user, login, signup, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
