// src/pages/Home.jsx
import React, { useEffect } from 'react';
import { Grid, Typography } from '@mui/material';
import SearchBar from '../components/SearchBar';
import MovieCard from '../components/MovieCard';
import FilterBar from '../components/FilterBar';
import { useMovie } from '../context/MovieContext';
import tmdb from '../api/tmdb';

export default function Home({ selectedGenre }) {
  const { movies, setMovies } = useMovie();

  useEffect(() => {
    const fetchTrending = async () => {
      try {
        const res = await tmdb.get('/trending/movie/week');
        const filteredMovies = selectedGenre
          ? res.data.results.filter(movie => movie.genre_ids.includes(parseInt(selectedGenre)))
          : res.data.results;
        setMovies(filteredMovies);
      } catch (error) {
        console.error('Failed to fetch trending movies:', error);
      }
    };
    fetchTrending();
  }, [setMovies, selectedGenre]);

  return (
    <div>
      <Typography variant="h4" sx={{ m: 2 }}>Trending Movies</Typography>
      <SearchBar />
      <FilterBar onFilterChange={(genre) => window.location.reload()} />
      <Grid container spacing={2}>
        {movies.map(movie => (
          <MovieCard key={movie.id} movie={movie} />
        ))}
      </Grid>
    </div>
  );
}
