// src/App.js
import React from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { MovieProvider } from './context/MovieContext';
import Home from './pages/Home';
import MoviePage from './pages/MoviePage';
import Favorites from './pages/Favorites'; // Make sure this file exists as Favorites.jsx
import Login from './pages/Login'; // Assuming Login page is implemented
import ThemeToggle from './components/ThemeToggle';
import { CssBaseline, Container } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import useLocalStorage from './hooks/useLocalStorage'; // custom hook for theme persistence

export default function App() {
  const [mode, setMode] = useLocalStorage('themeMode', 'light');

  const theme = createTheme({
    palette: {
      mode: mode,
    },
  });

  const toggleTheme = () => {
    setMode((prev) => (prev === 'light' ? 'dark' : 'light'));
  };

  return (
    <MovieProvider>
      <ThemeProvider theme={theme}>
        <CssBaseline />
        <BrowserRouter>
          <Container maxWidth="lg">
            <ThemeToggle mode={mode} toggleTheme={toggleTheme} />
            <Routes>
              <Route path="/login" element={<Login />} />
              <Route path="/" element={<Home />} />
              <Route path="/movie/:id" element={<MoviePage />} />
              <Route path="/favorites" element={<Favorites />} />
            </Routes>
          </Container>
        </BrowserRouter>
      </ThemeProvider>
    </MovieProvider>
  );
}
