// src/context/AuthContext.js
import React, { createContext, useContext, useState, useEffect } from 'react';
import { saveToLocalStorage, loadFromLocalStorage, removeFromLocalStorage } from '../utils/localStorageHelpers';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    const storedUser = loadFromLocalStorage('authUser');
    if (storedUser) setCurrentUser(storedUser);
  }, []);

  const signup = async (username, email, password) => {
    const existingUsers = loadFromLocalStorage('users') || [];

    // Check if email already exists
    const userExists = existingUsers.some((user) => user.email === email);
    if (userExists) return false;

    const newUser = { username, email, password };
    const updatedUsers = [...existingUsers, newUser];
    saveToLocalStorage('users', updatedUsers);
    saveToLocalStorage('authUser', newUser);
    setCurrentUser(newUser);
    return true;
  };

  const login = async (email, password) => {
    const users = loadFromLocalStorage('users') || [];
    const foundUser = users.find((user) => user.email === email && user.password === password);
    if (foundUser) {
      saveToLocalStorage('authUser', foundUser);
      setCurrentUser(foundUser);
      return true;
    }
    return false;
  };

  const logout = () => {
    removeFromLocalStorage('authUser');
    setCurrentUser(null);
  };

  return (
    <AuthContext.Provider value={{ currentUser, login, logout, signup }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
