// src/App.js
import React, { useState } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { CssBaseline, Container } from '@mui/material';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { MovieProvider } from './context/MovieContext';
import { AuthProvider } from './context/AuthContext';

import Home from './pages/Home';
import MoviePage from './pages/MoviePage';
import Favorites from './pages/Favorites';
import Login from './pages/Login';
import ThemeToggle from './components/ThemeToggle';
import FilterBar from './components/FilterBar';

import { loadFromLocalStorage, saveToLocalStorage } from './utils/localStorageHelpers';

export default function App() {
  // Load initial theme mode from localStorage or default to 'light'
  const initialMode = loadFromLocalStorage('themeMode') || 'light';
  const [mode, setMode] = useState(initialMode);
  const [selectedGenre, setSelectedGenre] = useState('');

  const theme = createTheme({
    palette: {
      mode: mode,
    },
  });

  const toggleTheme = () => {
    const newMode = mode === 'light' ? 'dark' : 'light';
    setMode(newMode);
    saveToLocalStorage('themeMode', newMode);
  };

  const handleFilterChange = (genreId) => {
    setSelectedGenre(genreId);
  };

  return (
    <AuthProvider>
      <MovieProvider>
        <ThemeProvider theme={theme}>
          <CssBaseline />
          <BrowserRouter>
            <Container maxWidth="lg" sx={{ py: 2 }}>
              <ThemeToggle mode={mode} toggleTheme={toggleTheme} />
              <FilterBar onFilterChange={handleFilterChange} />
              <Routes>
                <Route path="/login" element={<Login />} />
                <Route path="/" element={<Home selectedGenre={selectedGenre} />} />
                <Route path="/movie/:id" element={<MoviePage />} />
                <Route path="/favorites" element={<Favorites />} />
              </Routes>
            </Container>
          </BrowserRouter>
        </ThemeProvider>
      </MovieProvider>
    </AuthProvider>
  );
}
