// src/api/tmdb.js
import axios from 'axios';

const tmdb = axios.create({
  baseURL: 'https://api.themoviedb.org/3',
  params: {
    api_key: process.env.d0d79eb2a003c31938434d703d0cfc10,
    language: 'en-US'
  }
});

export default tmdb;
